const math = require('mathjs');

const generate = (case_ = 0) => {
    // ---- Unit Systems ----
    const unitSystems = {
        si: {
            speed: 'm/s',
            distance: 'm',
            time: 's'
        },
        uscs: {
            speed: 'ft/s',
            distance: 'ft',
            time: 's'
        }
    };  

    // ---- Hardcoded Values ----
    const PREDEFINED_VALUES = { speed: 10, time: 20, selectedSystem: 'si' }; // matches chosen system
    const TEST_VALUES = { speed: 10, time: 20 }; // hardcoded test values
    const CORRECT_ANSWER = 200; // hardcoded correct answer

    const ABS_TOL = 1e-6;
    const REL_TOL = 1e-6;

    let selectedSystem;
    let speed, time;
    let test_results = { pass: null, message: '' };

    if (case_ === 0) {
        // Random system selection
        selectedSystem = math.randomInt(0, 2) === 0 ? 'si' : 'uscs';
        units = unitSystems[selectedSystem];
        // Generate random values: speed between 5 to 30 (m/s or ft/s) and time between 1 to 10 seconds
        speed = math.randomInt(5, 31);
        time = math.randomInt(1, 11);
    } else if (case_ === 1) {
        // Use predefined values
        speed = PREDEFINED_VALUES.speed;
        time = PREDEFINED_VALUES.time;
        selectedSystem = PREDEFINED_VALUES.selectedSystem;
    } else if (case_ === 2) {
        if (typeof CORRECT_ANSWER === 'undefined') {
            throw new Error('Test case unavailable: CORRECT_ANSWER not provided');
        }
        // Use test values
        speed = TEST_VALUES.speed;
        time = TEST_VALUES.time;
        selectedSystem = 'si'; // Lock to SI for testing
    } else {
        throw new Error(`Unknown case_: ${case_}`);
    }

    const units = unitSystems[selectedSystem];

    // Calculate the total distance: distance = speed * time
    const distance = speed * time;

    // ---- Verification Logic for Case 2 ----
    if (case_ === 2) {
        const expected = CORRECT_ANSWER;
        const diff = Math.abs(distance - expected);
        const tol = Math.max(ABS_TOL, REL_TOL * Math.abs(expected));
        const passed = diff <= tol;

        test_results.pass = passed ? 1 : 0;
        test_results.message = passed
            ? `PASS — computed=${distance} ${units.distance}, expected=${expected} ${units.distance}`
            : `FAIL — computed=${distance} ${units.distance}, expected=${expected} ${units.distance}`;
    }

    // Return the structured data
    return {
        params: {
            speed: speed,
            time: time,
            unitsSpeed: units.speed,
            unitsDist: units.distance,
            unitsTime: units.time
        },
        correct_answers: {
            distance: math.round(distance, 3) // Round to 3 decimal places
        },
        test_results
    };
};

module.exports = {
    generate
};

const math = require('mathjs');

const generate = (case_ = 0) => {
    const unitSystems = {
        si: {
            speed: 'km/h',
            distance: 'km',
            time: 'h'
        },
        uscs: {
            speed: 'mph',
            distance: 'ft',
            time: 's'
        }
    };

    // Hardcoded Values
    const PREDEFINED_VALUES = { speed: 60, time: 2 }; // Speed in km/h and time in hours
    const TEST_VALUES = { speed: 60, time: 2 }; // Same as PREDEFINED_VALUES for testing
    const CORRECT_ANSWER = 120; // Correct answer for distance in km

    const ABS_TOL = 1e-6;
    const REL_TOL = 1e-6;

    const selectedSystem = (case_ === 2) ? 'si' : (math.randomInt(0, 2) === 0 ? 'si' : 'uscs');
    const units = unitSystems[selectedSystem];

    let speed, time;
    const test_results = { pass: null, message: "" };

    if (case_ === 0) {
        speed = math.randomInt(30, 121);
        time = math.randomInt(1, 10);
    } else if (case_ === 1) {
        speed = PREDEFINED_VALUES.speed;
        time = PREDEFINED_VALUES.time;
    } else if (case_ === 2) {
        if (typeof CORRECT_ANSWER === "undefined") {
            throw new Error("Test case unavailable: CORRECT_ANSWER not provided");
        }
        speed = TEST_VALUES.speed;
        time = TEST_VALUES.time;
    } else {
        throw new Error(`Unknown case_: ${case_}`);
    }

    // Calculate distance: 
    let distance;
    if (units.speed === 'km/h') {
        distance = speed * time; // in km
    } else { // units.speed === 'mph'
        distance = speed * time * 0.000189394; // convert mph to km
    }

    // Handling Case 2 for verification with the correct answer
    if (case_ === 2) {
        const expected = CORRECT_ANSWER;
        const diff = Math.abs(distance - expected);
        const tol = Math.max(ABS_TOL, REL_TOL * Math.abs(expected));
        const passed = diff <= tol;

        test_results.pass = passed ? 1 : 0;
        test_results.message = passed
            ? `PASS — computed=${distance} ${units.distance}, expected=${expected} ${units.distance}`
            : `FAIL — computed=${distance} ${units.distance}, expected=${expected} ${units.distance}`;
    }

    return {
        params: {
            speed,
            time,
            unitsSpeed: units.speed,
            unitsTime: units.time,
            unitsDist: units.distance,
        },
        correct_answers: {
            distance: Number(math.round(distance, 3)),
        },
        test_results,
    };
};

module.exports = { generate };
